import React, { useState } from "react";
import "./Expense.css";
import ExpenseItem from "./ExpenseItem";
import Card from '../UI/Card';
import ExpensesFilter from "./ExpenseFilter";
import { set } from "mongoose";

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState('2023');
 const [filterInfoText, setFilterInfoText]=  useState('2022,2024 & 2025')


  const filterChangeHandler = selectedYear => {
       setFilteredYear(selectedYear);
       if(selectedYear === '2022'){
        setFilterInfoText('2023, 2024 & 2025')
      } else if(selectedYear === '2023'){
        setFilterInfoText('2022,2024 & 2025')
      } else if (selectedYear === '2024'){
        setFilterInfoText('2022,2023 & 2025')
      } else if (selectedYear === '2025'){
        setFilterInfoText ('2022,2023 & 2024')
      }
      
  }
  return (
    <div>
     <Card className="expenses">
      <ExpensesFilter selected = {filteredYear} onChangeFilter ={filterChangeHandler}/>
      <p> {setFilterInfoText}</p>
      <ExpenseItem
        title={props.items[0].title}
        amount={props.items[0].amount}
        date={props.items[0].date}
        
      />
      <ExpenseItem
        title={props.items[1].title}
        amount={props.items[1].amount}
        date={props.items[1].date}
      />
      <ExpenseItem
        title={props.items[2].title}
        amount={props.items[2].amount}
        date={props.items[2].date}
      />
      <ExpenseItem
        title={props.items[3].title}
        amount={props.items[3].amount}
        date={props.items[3].date}
      />
    </Card>
    </div>
    
  );
}

export default Expenses;
